# 单个值校验类型的展示单元转换
def value_check_util_transform(result_dict, field_name, show_name = None):
    if show_name == None:
        show_name = field_name
    pass_boo = None
    audit_item = result_dict[field_name]
    format_item = {"title": show_name}
    if audit_item["helpfulness"] == 1:
        format_item["result"] = "通过"
        pass_boo = True
    else:
        format_item["result"] = "不通过"
        pass_boo = False
    format_item["tip"] = audit_item["audit_tips"]
    format_item["suggestion"] = audit_item["suggestion"]
    format_item["content"] = ["".join([each["text"] for each in json.loads(audit_item["context"])])]
    format_item["diff"] = ""
    return {"pass_boo":pass_boo,"format_item":format_item}

# 条款校验类型的展示单元转换
